import React from 'react';
import { useNavigate } from 'react-router-dom';
import Login from './Login';

const AuthPage = () => {
  const navigate = useNavigate();

  return (
    <div
      style={{
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '2rem',
        background: 'linear-gradient(135deg, #667eea, #764ba2)',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <div
        style={{
          display: 'flex',
          gap: '3rem',
          backgroundColor: 'rgba(255, 255, 255, 0.9)',
          borderRadius: '12px',
          boxShadow: '0 8px 24px rgba(0,0,0,0.15)',
          padding: '2rem 3rem',
          maxWidth: '700px',
          width: '100%',
          flexWrap: 'wrap',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <div
          style={{
            flex: '1 1 400px',
            backgroundColor: '#fff',
            padding: '2rem',
            borderRadius: '10px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
          }}
        >
          <h2 style={{ textAlign: 'center', marginBottom: '1.5rem', color: '#333' }}>Login</h2>
          <Login />
        </div>

        <div
          style={{
            flex: '0 0 200px',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            backgroundColor: '#fff',
            padding: '2rem',
            borderRadius: '10px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            cursor: 'pointer',
          }}
          onClick={() => navigate('/register')}
          role="button"
          tabIndex={0}
          onKeyDown={(e) => { if(e.key === 'Enter') navigate('/register') }}
        >
          <h2 style={{ marginBottom: '1rem', color: '#333' }}>New here?</h2>
          <button
            style={{
              backgroundColor: '#667eea',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              padding: '0.75rem 1.5rem',
              fontSize: '1rem',
              fontWeight: '600',
              cursor: 'pointer',
              boxShadow: '0 4px 12px rgba(102,126,234,0.6)',
              transition: 'background-color 0.3s ease',
            }}
            onClick={() => navigate('/register')}
            onKeyDown={(e) => e.stopPropagation()}
          >
            Register
          </button>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
