import React from 'react';
import { useNavigate } from 'react-router-dom';
import Login from './Login';

const AuthPage = () => {
  const navigate = useNavigate();

  return (
    <div
      style={{
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '3rem 1.5rem',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <div
        style={{
          display: 'flex',
          flexDirection: 'row',
          gap: '3rem',
          backgroundColor: 'rgba(255, 255, 255, 0.95)',
          borderRadius: '20px',
          boxShadow: '0 15px 40px rgba(0,0,0,0.25)',
          padding: '3rem 4rem',
          maxWidth: '820px',
          width: '100%',
          flexWrap: 'wrap',
          justifyContent: 'center',
          alignItems: 'stretch',
        }}
      >
        {/* Login Card */}
        <div
          style={{
            flex: '1 1 420px',
            backgroundColor: '#fff',
            padding: '3rem 3rem 2.5rem',
            borderRadius: '18px',
            boxShadow: '0 10px 32px rgba(0,0,0,0.12)',
            display: 'flex',
            flexDirection: 'column',
          }}
        >
          <h2
            style={{
              color: '#222',
              fontWeight: '700',
              fontSize: '2.4rem',
              letterSpacing: '0.06em',
              marginBottom: '2rem',
              textAlign: 'center',
              userSelect: 'none',
            }}
          >
            Welcome Back
          </h2>
          <Login />
        </div>

        {/* Register Card */}
        <div
          role="button"
          tabIndex={0}
          onClick={() => navigate('/register')}
          onKeyDown={(e) => {
            if (e.key === 'Enter') navigate('/register');
          }}
          style={{
            flex: '0 0 260px',
            background: 'linear-gradient(135deg, #7b61ff, #4e3cc0)',
            borderRadius: '18px',
            boxShadow: '0 10px 32px rgba(123, 97, 255, 0.6)',
            color: 'white',
            cursor: 'pointer',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            padding: '3rem 2rem',
            userSelect: 'none',
            transition: 'background 0.3s ease, box-shadow 0.3s ease',
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.background = 'linear-gradient(135deg, #5a3ed8, #3a2fa4)';
            e.currentTarget.style.boxShadow = '0 15px 48px rgba(58, 47, 164, 0.8)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.background = 'linear-gradient(135deg, #7b61ff, #4e3cc0)';
            e.currentTarget.style.boxShadow = '0 10px 32px rgba(123, 97, 255, 0.6)';
          }}
        >
          <h3
            style={{
              fontWeight: '700',
              fontSize: '2rem',
              marginBottom: '2rem',
              userSelect: 'none',
              letterSpacing: '0.05em',
            }}
          >
            New Here?
          </h3>
          <button
            onClick={(e) => {
              e.stopPropagation();
              navigate('/register');
            }}
            onKeyDown={(e) => e.stopPropagation()}
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '0.8rem',
              backgroundColor: 'rgba(255,255,255,0.9)',
              color: '#4e3cc0',
              border: 'none',
              borderRadius: '12px',
              padding: '0.9rem 2.5rem',
              fontSize: '1.15rem',
              fontWeight: '700',
              cursor: 'pointer',
              boxShadow: '0 7px 22px rgba(78, 60, 192, 0.5)',
              transition: 'background-color 0.3s ease, color 0.3s ease',
              userSelect: 'none',
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.backgroundColor = '#f3f0ff';
              e.currentTarget.style.color = '#3629b2';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.backgroundColor = 'rgba(255,255,255,0.9)';
              e.currentTarget.style.color = '#4e3cc0';
            }}
          >
            Register
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="22"
              height="22"
              fill="none"
              stroke="#4e3cc0"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
              viewBox="0 0 24 24"
              aria-hidden="true"
              focusable="false"
            >
              <line x1="5" y1="12" x2="19" y2="12" />
              <polyline points="12 5 19 12 12 19" />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
