import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Register() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Flicker animation for neon flicker effect
  useEffect(() => {
    const flicker = () => {
      const root = document.documentElement;
      root.style.setProperty('--flicker-opacity', (Math.random() * 0.15 + 0.85).toString());
    };
    const interval = setInterval(flicker, 100);
    return () => clearInterval(interval);
  }, []);

  const register = async () => {
    if (!username || !email || !password) {
      setError('Please fill all fields');
      return;
    }
    setLoading(true);
    setError(null);
    try {
      const res = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, email, password }),
      });
      if (!res.ok) throw new Error('Failed to register');
      setLoading(false);
      navigate('/login');
    } catch (err) {
      setLoading(false);
      setError(err.message || 'Something went wrong');
    }
  };

  return (
    <div className="register-container">
      <h1>Register</h1>
      {error && <div className="error">{error}</div>}

      <input
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        disabled={loading}
        className="neon-input"
      />
      <input
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        disabled={loading}
        className="neon-input"
      />
      <input
        placeholder="Password"
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        disabled={loading}
        className="neon-input"
      />

      <button
        onClick={register}
        disabled={loading}
        className="neon-button"
      >
        {loading ? 'Registering...' : 'Register'}
      </button>

      <style>{`
        :root {
          --neon-cyan: #00fff7;
          --neon-pink: #ff00ff;
          --neon-glow1: 0 0 5px var(--neon-cyan);
          --neon-glow2: 0 0 20px var(--neon-cyan);
          --flicker-opacity: 1;
        }

        .register-container {
          max-width: 420px;
          margin: 4rem auto;
          padding: 3rem 2rem;
          background: rgba(10, 0, 20, 0.85);
          border-radius: 25px;
          box-shadow:
            0 0 20px var(--neon-pink),
            inset 0 0 40px var(--neon-cyan),
            inset 0 0 80px var(--neon-pink);
          color: var(--neon-cyan);
          font-family: 'Orbitron', sans-serif;
          text-align: center;
          user-select: none;
          filter: drop-shadow(0 0 4px var(--neon-cyan));
          animation: flicker 3s infinite;
        }

        @keyframes flicker {
          0%, 100% { opacity: var(--flicker-opacity); }
          50% { opacity: calc(var(--flicker-opacity) - 0.1); }
        }

        h1 {
          font-weight: 900;
          font-size: 3rem;
          margin-bottom: 2rem;
          text-transform: uppercase;
          letter-spacing: 0.2em;
          text-shadow:
            0 0 8px var(--neon-cyan),
            0 0 20px var(--neon-pink),
            0 0 30px var(--neon-pink);
        }

        .error {
          background: #ff0055;
          padding: 0.75rem 1rem;
          border-radius: 12px;
          margin-bottom: 1.5rem;
          font-weight: 700;
          box-shadow: 0 0 15px #ff0055;
          color: white;
          user-select: text;
        }

        .neon-input {
          width: 100%;
          margin-bottom: 20px;
          padding: 1rem 1.2rem;
          border-radius: 20px;
          border: 2px solid var(--neon-cyan);
          background: rgba(0, 0, 0, 0.3);
          color: var(--neon-cyan);
          font-size: 1.1rem;
          outline: none;
          box-shadow:
            inset 0 0 8px var(--neon-cyan),
            0 0 10px var(--neon-cyan);
          font-family: 'Orbitron', sans-serif;
          transition: border-color 0.3s ease, box-shadow 0.3s ease;
          text-align: center;
          user-select: text;
        }
        .neon-input::placeholder {
          color: var(--neon-cyan);
          opacity: 0.7;
        }
        .neon-input:focus {
          border-color: var(--neon-pink);
          box-shadow:
            inset 0 0 20px var(--neon-pink),
            0 0 25px var(--neon-pink);
        }

        .neon-button {
          margin-top: 1.5rem;
          padding: 1rem 3rem;
          border-radius: 40px;
          border: none;
          background:
            linear-gradient(45deg, var(--neon-cyan), var(--neon-pink));
          color: #000;
          font-weight: 900;
          font-size: 1.3rem;
          cursor: pointer;
          box-shadow:
            0 0 15px var(--neon-cyan),
            0 0 30px var(--neon-pink),
            0 0 50px var(--neon-pink);
          user-select: none;
          text-transform: uppercase;
          font-family: 'Orbitron', sans-serif;
          transition: filter 0.3s ease, transform 0.2s ease;
        }
        .neon-button:disabled {
          filter: grayscale(80%) opacity(0.6);
          cursor: wait;
          box-shadow: none;
        }
        .neon-button:hover:not(:disabled) {
          filter: drop-shadow(0 0 20px var(--neon-pink));
          transform: scale(1.05);
        }
        .neon-button:active:not(:disabled) {
          transform: scale(0.95);
        }
      `}</style>
    </div>
  );
}
