import React from 'react';
import { useNavigate } from 'react-router-dom';
import Login from './Login';

const AuthPage = () => {
  const navigate = useNavigate();

  return (
    <div
      style={{
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '3rem',
        background: 'linear-gradient(135deg, #667eea, #764ba2)',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <div
        style={{
          display: 'flex',
          gap: '3.5rem',
          backgroundColor: 'rgba(255, 255, 255, 0.95)',
          borderRadius: '16px',
          boxShadow: '0 12px 36px rgba(0,0,0,0.18)',
          padding: '3rem 4rem',
          maxWidth: '720px',
          width: '100%',
          flexWrap: 'wrap',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <div
          style={{
            flex: '1 1 420px',
            backgroundColor: '#fff',
            padding: '3rem 2.5rem',
            borderRadius: '14px',
            boxShadow: '0 8px 28px rgba(0,0,0,0.12)',
          }}
        >
          <h2
            style={{
              textAlign: 'center',
              marginBottom: '2rem',
              color: '#222',
              fontWeight: '700',
              fontSize: '2rem',
              letterSpacing: '0.05em',
            }}
          >
            Welcome Back
          </h2>
          <Login />
        </div>

        <div
          role="button"
          tabIndex={0}
          onClick={() => navigate('/register')}
          onKeyDown={(e) => { if (e.key === 'Enter') navigate('/register') }}
          style={{
            flex: '0 0 230px',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            backgroundColor: '#6c63ff',
            padding: '3rem 2rem',
            borderRadius: '14px',
            boxShadow: '0 8px 28px rgba(108, 99, 255, 0.5)',
            color: 'white',
            cursor: 'pointer',
            userSelect: 'none',
            transition: 'background-color 0.3s ease, box-shadow 0.3s ease',
          }}
          onMouseEnter={e => {
            e.currentTarget.style.backgroundColor = '#574fd6';
            e.currentTarget.style.boxShadow = '0 12px 40px rgba(87, 79, 214, 0.7)';
          }}
          onMouseLeave={e => {
            e.currentTarget.style.backgroundColor = '#6c63ff';
            e.currentTarget.style.boxShadow = '0 8px 28px rgba(108, 99, 255, 0.5)';
          }}
        >
          <h3
            style={{
              marginBottom: '1.5rem',
              fontWeight: '600',
              fontSize: '1.8rem',
              letterSpacing: '0.06em',
            }}
          >
            New Here?
          </h3>
          <button
            onClick={(e) => {
              e.stopPropagation();
              navigate('/register');
            }}
            onKeyDown={(e) => e.stopPropagation()}
            style={{
              backgroundColor: 'white',
              color: '#6c63ff',
              border: 'none',
              borderRadius: '8px',
              padding: '0.85rem 2.5rem',
              fontSize: '1.1rem',
              fontWeight: '700',
              cursor: 'pointer',
              boxShadow: '0 6px 20px rgba(108, 99, 255, 0.4)',
              transition: 'background-color 0.3s ease, color 0.3s ease',
              display: 'flex',
              alignItems: 'center',
              gap: '0.6rem',
            }}
            onMouseEnter={e => {
              e.currentTarget.style.backgroundColor = '#e0dfff';
              e.currentTarget.style.color = '#574fd6';
            }}
            onMouseLeave={e => {
              e.currentTarget.style.backgroundColor = 'white';
              e.currentTarget.style.color = '#6c63ff';
            }}
          >
            Register{' '}
            <span style={{ fontSize: '1.3rem', lineHeight: '1' }} aria-hidden="true">
              âž”
            </span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
