import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Register() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const register = async () => {
    if (!username || !email || !password) {
      setError('Please fill all fields');
      return;
    }
    setLoading(true);
    setError(null);
    try {
      const res = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, email, password }),
      });
      if (!res.ok) throw new Error('Failed to register');
      setLoading(false);
      navigate('/login');
    } catch (err) {
      setLoading(false);
      setError(err.message || 'Something went wrong');
    }
  };

  return (
    <div
      style={{
        maxWidth: 400,
        margin: 'auto',
        background: 'rgba(0,0,0,0.7)',
        padding: '3rem 2rem',
        borderRadius: 20,
        boxShadow:
          '0 0 10px #0ff, 0 0 30px #0ff, inset 0 0 40px #0ff, inset 0 0 80px #0ff',
        color: '#0ff',
        fontFamily: "'Orbitron', sans-serif",
        textAlign: 'center',
        userSelect: 'none',
      }}
    >
      <h1
        style={{
          fontWeight: '900',
          fontSize: '2.8rem',
          marginBottom: '1.5rem',
          textShadow:
            '0 0 10px #0ff, 0 0 30px #0ff, 0 0 40px #0ff, 0 0 60px #0ff',
          letterSpacing: '0.15em',
          textTransform: 'uppercase',
        }}
      >
        Register
      </h1>

      {error && (
        <div
          style={{
            marginBottom: 16,
            padding: '0.75rem',
            background: 'rgba(255,0,0,0.7)',
            borderRadius: 10,
            color: '#fff',
            fontWeight: '700',
          }}
        >
          {error}
        </div>
      )}

      <input
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        style={inputStyle}
        disabled={loading}
      />
      <input
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        style={inputStyle}
        disabled={loading}
      />
      <input
        placeholder="Password"
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        style={inputStyle}
        disabled={loading}
      />

      <button
        onClick={register}
        disabled={loading}
        style={{
          marginTop: '1.5rem',
          padding: '0.9rem 2rem',
          borderRadius: 30,
          border: 'none',
          background:
            'linear-gradient(45deg, #00fff7, #ff00ff)',
          color: '#000',
          fontWeight: '900',
          fontSize: '1.25rem',
          cursor: loading ? 'wait' : 'pointer',
          boxShadow:
            '0 0 12px #00fff7, 0 0 18px #ff00ff, 0 0 30px #00fff7',
          transition: 'transform 0.2s ease',
          userSelect: 'none',
          textTransform: 'uppercase',
        }}
        onMouseEnter={(e) => {
          if (!loading) e.currentTarget.style.transform = 'scale(1.1)';
        }}
        onMouseLeave={(e) => {
          if (!loading) e.currentTarget.style.transform = 'scale(1)';
        }}
      >
        {loading ? 'Registering...' : 'Register'}
      </button>
    </div>
  );
}

const inputStyle = {
  width: '100%',
  marginBottom: 20,
  padding: '1rem 1.2rem',
  borderRadius: 15,
  border: '2px solid #0ff',
  background: 'rgba(0,0,0,0.5)',
  color: '#0ff',
  fontSize: '1rem',
  outline: 'none',
  boxShadow: '0 0 8px #0ff inset',
  fontFamily: "'Orbitron', sans-serif",
  textAlign: 'center',
  userSelect: 'text',
  transition: 'border-color 0.3s ease',
};
